export const reducer = (state,action)=>{
    switch (action.type){
        case 'GET_DENTISTS':
            return{...state, list: action.payload}
        case 'GET_DENTIST':
            return{...state, dentistDetail: action.payload}
        case 'ADD_FAV':
            return{loc}//no añadir a favorito solo guardar en local storagfe
    }
}
